import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const navigate = useNavigate();
const CarCreate = () => {
  const [carData, setCarData] = useState({
    title: '',
    description: '',
    tags: '',
    images: []
  });

  const [loading, setLoading] = useState(false);
  const history = useHistory();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setCarData((prevData) => ({
      ...prevData,
      [name]: value
    }));
  };

  const handleImageUpload = (e) => {
    const files = e.target.files;
    setCarData((prevData) => ({
      ...prevData,
      images: [...prevData.images, ...files]
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    const formData = new FormData();
    formData.append('title', carData.title);
    formData.append('description', carData.description);
    formData.append('tags', carData.tags);
    
    for (let i = 0; i < carData.images.length; i++) {
      formData.append('images', carData.images[i]);
    }

    try {
      await axios.post('http://localhost:5000/api/cars/create', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
          Authorization: `Bearer ${localStorage.getItem('token')}`
        }
      });
      navigate('/cars');
    } catch (error) {
      console.error(error);
      alert('Error creating car');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="car-create">
      <h2>Create a New Car</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Title</label>
          <input
            type="text"
            name="title"
            value={carData.title}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label>Description</label>
          <textarea
            name="description"
            value={carData.description}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label>Tags</label>
          <input
            type="text"
            name="tags"
            value={carData.tags}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label>Images</label>
          <input type="file" multiple onChange={handleImageUpload} />
        </div>
        <button type="submit" disabled={loading}>
          {loading ? 'Creating...' : 'Create Car'}
        </button>
      </form>
    </div>
  );
};

export default CarCreate;
